package com.example.AssignmentOperator;

public class DifferentAssignmentOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 10;
		System.out.println(i += 5);	//output is 15
		System.out.println(i -= 5);	//output is 10 as i is now 15
		System.out.println(i *= 5);	//output is 50 as i is now 10
		System.out.println(i /= 5);	//output is 10 as i is now 50
	}

}
